                        var cartridge = cartridges.shift();

                        if (cartridge) {
                            started.push(cartridge);
                            this.addCartridge(cartridge, false).then(add).otherwise(hitch(deferred, 'reject'));
                        } else {
                            deferred.resolve(this);
                        }
